package SelJavaTestPackage;

public class Swap2numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float first = 99.87f;
		float second =20.64f;
		float temp;
		System.out.println("The first and Second Numbers before swap are: " +first+","+second);
		temp = first;
		first=second;
		second=temp;
		System.out.println("The first and Second Numbers after swap are: " +first+","+second);
		

	}

}
